mapper.select:

SELECT p.post_id, p.category_id, p.title, p.is_new, p.start_date, p.end_date, p.agree_count
FROM post_keyword pk
INNER JOIN post p
ON pk.post_id = p.post_id
WHERE pk.keyword = #{keyword}
	AND p.is_new = #{isNew}
ORDER BY p.agree_count DESC
LIMIT 1;

    	SELECT *
		FROM post
		WHERE category_id = #{categoryId}
		ORDER BY agree_count DESC
		LIMIT 1;
